import { useState } from "react";
import { Form, useNavigate, useNavigation, json, redirect } from "react-router-dom";

function HostRegisterForm({method}){
    const navigate = useNavigate();
    const navigation = useNavigation();
    const isSubmiting = navigation.state === "submitting";

    const[file, setFile] = useState('');
    const [files, setFiles] = useState([]);
    
    function cancelHandler() {
        navigate("..");
      }

    const onMainFile = file => {
        setFile(file);
        console.log(file);
    }
    
    const onSaveFiles = files => {
        setFiles(files);
        console.log(files);
    }

    return (
        <Form method={method}>
            <label htmlFor="shortintro">농장이름</label>
            <input type="text" name="shortintro" id="shortintro" />
            <br />

            <label htmlFor="region">지역</label>
            <input type="radio" name="region" id="region" value="1"/><label>경기도</label>
            <input type="radio" name="region" id="region" value="2"/><label>충청도</label>
            <br />

            <label htmlFor="age">나이</label>
            <input type="radio" name="age" id="age" value="1"/><label>20</label>
            <input type="radio" name="age" id="age" value="2"/><label>30</label>
            <br />
            
            <label htmlFor="gender">성별</label>
            <input type="radio" name="gender" id="gender" value="1"/><label>여</label>
            <input type="radio" name="gender" id="gender" value="2"/><label>남</label>
            <br />

            <label htmlFor="farmsts">농법</label>
            <input type="radio" name="farmsts" id="farmsts" value="1"/><label>친환경</label>
            <input type="radio" name="farmsts" id="farmsts" value="2"/><label>유기농</label>
            <br />

            <label htmlFor="maxPpl">최대인원</label>
            <input type="text" name="maxPpl" id="maxPpl" />
            <br />

            <label htmlFor="intro">소개</label>
            <textarea name="intro"></textarea>
            <br />

            <label htmlFor="lat">위도</label>
            <input type="text" name="lat" id="lat" />
            <br />

            <label htmlFor="lng">경도</label>
            <input type="text" name="lng" id="lng" />
            <br />
            <div>
                <label htmlFor="file">대표이미지</label>
                <input type="file" name={file} onChange={onMainFile} />
                <button >파일 업로드</button> 
                {/* onClick={onFileUpload} */}
            </div>
            <div>
                <label htmlFor="files" onChange={onSaveFiles}>이미지</label>
                <input type="file" name={files} multiple />
                <span >파일 업로드</span> 
                {/* onClick={onFileUpload} */}
            </div>

            {/* 승인날짜, 승인여부 */}
            <div>
                <button type="button" onClick={cancelHandler} disabled={isSubmiting}>
                취소
                </button>
                <button disabled={isSubmiting}>등록</button>
            </div>
        

        </Form>
        
    )
}
export default HostRegisterForm;

export async function action({request, params}){
    const method = request.method;
    const data = await request.formData();

    const HostData = {
        shortintro : data.get('shortintro'),
        region : data.get('region'),
        age : data.get('age'),
        gender : data.get('gender'),
        farmsts : data.get('farmsts'),
        maxPpl : data.get('maxPpl'),
        intro : data.get('intro'),
        lat : data.get('lat'),
        lng : data.get('lng'),
    }

    const formData = new FormData();
    formData.append("hostData", new Blob([JSON.stringify(HostData)], {type :"application/json"}));

    data.files.array.forEach(element => {
        
    });


}
