import { Link } from "react-router-dom";
import { useEffect, useRef, useState } from "react";
import isEqual from "lodash-es"

const HostList = (props) => {
  const [existHost, setExistHost] = useState(false); // 아무것도 없음
  const prevHostList = useRef(); // 이전에 호스트 리스트를 담음 

  useEffect(() => {
    console.log("매번 렌더링 마다 실행됨 ");
    if(props.hosts && !isEqual(props.hosts,prevHostList.current)){
      console.log("호스트 리스트 검색결과 변경시 실행됨")
      prevHostList.current = props.hosts;
      setExistHost(true);

    }
  });

  return (
    <>
    {!existHost &&<p>검색결과없음</p>}
      <ul>
        {existHost && props.hosts.map((host) => (
          <li key={host.hnum}>
            <img
              src={host.hostMainImg.fileUri}
              alt={host.hostMainImg.filename}
              style={{ width: "200px", height: "150px" }}
            />
            <Link to={host.hnum}>{host.shortintro}</Link>
          </li>
        ))}
      </ul>
    </>
  );
};

export default HostList;
