    console.log(temp)
    return <BoardItem board = {temp} />

}

export default BoardDetailPage;

export async function loader({ request, param }) {
  const id = param.boardNum; 
  // 2. board-detail 에 매칭된 loader가 실행, 그때 param으로 path 들어옴   

  const response = await fetch("http://localhost:8080/api/post/" + id);
  if (!response.ok) {
    throw json(
      { message: "Could not fetch events." },
      {
        status: 500,
      }
    );
  } else{
    return response;
  }
}
